INSERT INTO public.vacancy (raw_data, search_request_id, id) VALUES ('{"id": "21591173", "area": {"id": "1", "url": "https://api.hh.ru/areas/1", "name": "Москва"}, "code": null, "name": "Java Chief Software Architect", "site": {"id": "hh", "name": "hh.ru"}, "test": null, "type": {"id": "open", "name": "Открытая"}, "hidden": false, "salary": {"to": 480000, "from": 450000, "gross": true, "currency": "RUR"}, "address": {"lat": 30.256812, "lng": -97.746701, "raw": null, "city": "Austin", "metro": null, "street": "Congress Ave S", "building": "401", "description": null, "metro_stations": []}, "premium": false, "archived": false, "contacts": null, "employer": {"id": "1482197", "url": "https://api.hh.ru/employers/1482197", "name": "Crossover", "trusted": true, "logo_urls": {"90": "https://hhcdn.ru/employer-logo/1763514.png", "240": "https://hhcdn.ru/employer-logo/1763515.png", "original": "https://hhcdn.ru/employer-logo-original/330291.png"}, "alternate_url": "https://hh.ru/employer/1482197", "vacancies_url": "https://api.hh.ru/vacancies?employer_id=1482197"}, "schedule": {"id": "remote", "name": "Удаленная работа"}, "relations": [], "created_at": "2017-06-27T15:41:34+0300", "department": null, "employment": {"id": "full", "name": "Полная занятость"}, "experience": {"id": "moreThan6", "name": "Более 6 лет"}, "key_skills": [{"name": "Java"}, {"name": "Java EE"}, {"name": "NoSQL"}, {"name": "Software Development"}], "description": "<p>Job Description:</p> <p>As the owner for the architecture and technical delivery of a multi-million dollar revenue per year enterprise software product, you will effectively work as the CTO of the engagement with a startup mindset, wearing multiple hats from lead architect and developer on the most complex elements of the product to technical team leader and solution evangelist. It’s your responsibility to ensure that the customers are blown away by the technical differentiation and quality of the product. We want to give you the ability to look at code in ways it has never been looked at before, to understand the interdependency in the code.</p> <p>We are empowering software development and creating higher quality code by using the latest technologies and toolsets. We are excited about using data to make decisions, we are bringing big data and analysis to software development. We are on the cutting edge of software development, and we innovate improvements on the newest technologies. We want people who are adaptable and can learn new things, and can use new ideas and concepts to solve business problems.<br /><br /><strong>Key Responsibilities:</strong></p> <ul> <li> <p>Developing expertise in one or more of our proprietary enterprise software products.</p> </li> <li> <p>Leveraging your broad technology capability set and maintain a level of excitement in using various technologies to architect, develop, and iteratively deploy real enterprise class software.</p> </li> <li> <p>Achieving an expert level understanding of our customers’ environments and their use of our products.</p> </li> <li> <p>Architecting and leading weekly technical delivery of one or more products in a way that maintains an unyielding focus on quality while matching the needs of the target customer base.</p> </li> <li> <p>Creating solutions architecture, algorithms, and designs for solutions that scale to the customer&#39;s enterprise/global requirements</p> </li> <li> <p>Leading a small team of Software Engineers to apply software engineering practices and implement automations across all elements of solution delivery</p> </li> <li> <p>Manage accountability of team members, with pinpoint focus on quality; Personally accountable for resolution of the most technically challenging issues</p> </li> </ul> <p><strong>Required Education, Experience and Qualities:</strong></p> <ul> <li> <p>Bachelor&#39;s degree in Computer Science, Computer Engineering, or Electrical Engineering</p> </li> <li> <p>At least 8 years of experience on hands-on Java development and related technologies</p> </li> <li> <p>Has worked with back end technologies based on both J2EE and latest frameworks like Spring.</p> </li> <li> <p>Has experience working with two or more of data related technologies and familiarity with others from various Relational databases, Nosql databases, Caching systems, Full Text Search engines, Big Data systems.</p> </li> <li> <p>Has good experience with the TDD approach, doing unit testing, mocking, integration/UI testing, behaviour driver testing.</p> </li> <li> <p>Has good experience working with, setting up and configuring any continuous integration and delivery systems, any code quality automation technologies.</p> </li> <li> <p>Has good experience working with one or more of architecture patterns and familiarity with others.</p> </li> <li> <p>Can resolve serious performance related issues by using various techniques, including testing, debugging and profiling.</p> </li> <li> <p>Has experience with one or more cloud or devops services like AWS, Docker, Heroku, Digitalocean, etc.</p> </li> <li> <p>Has experience with one or more front end technologies including templating, CSS and JS frameworks.</p> </li> <li> <p>Has experience or familiarity with any of Virtualization or Containerization technologies.</p> </li> <li> <p>Has experience or familiarity with any of Message Queuing/Broking or Service Bus systems.</p> </li> <li> <p>Can architect and design apps, review design and code, review and fix tests and functional coverage.</p> </li> <li> <p>Has good current hands-on development experience.</p> </li> <li> <p>Has experience working with complete product life cycles, quality metrics, evaluating technologies, helping with estimation and budgeting, helping with planning and road maps, helping with pre-sales and technical proposals.</p> </li> <li> <p>Excellent understanding of current enterprise software technologies and development practices/tools, including virtual environments, source control, remote development, issue tracking, build and test automation, and networking management</p> </li> <li> <p>Ability to engage sophisticated global customers in deep technical discussions, making them confident that you know their problem better than they do</p> </li> <li> <p>A true “roll up the sleeves and get it done” working approach; demonstrated success as a problem solver, operating as a result-oriented, self-starter</p> </li> <li> <p>Comfort “working virtually” with teammates and customers around the world</p> </li> </ul> <p>Hours: 40 hours/week, long term<br />Location: Global (remote)</p> <p> </p>", "billing_type": {"id": "standard", "name": "Стандарт"}, "published_at": "2017-06-27T15:41:34+0300", "response_url": null, "alternate_url": "https://hh.ru/vacancy/21591173", "allow_messages": true, "specializations": [{"id": "1.221", "name": "Программирование, Разработка", "profarea_id": "1", "profarea_name": "Информационные технологии, интернет, телеком"}, {"id": "1.3", "name": "CTO, CIO, Директор по IT", "profarea_id": "1", "profarea_name": "Информационные технологии, интернет, телеком"}, {"id": "1.327", "name": "Управление проектами", "profarea_id": "1", "profarea_name": "Информационные технологии, интернет, телеком"}], "negotiations_url": null, "accept_handicapped": false, "apply_alternate_url": "https://hh.ru/applicant/vacancy_response?vacancyId=21591173", "branded_description": null, "suitable_resumes_url": null, "quick_responses_allowed": false, "response_letter_required": false}', 3, '21591173');
