INSERT INTO public.vacancy (raw_data, search_request_id, id) VALUES ('{"id": "21815133", "area": {"id": "1", "url": "https://api.hh.ru/areas/1", "name": "Москва"}, "code": null, "name": "Специалист JIRA", "site": {"id": "hh", "name": "hh.ru"}, "test": null, "type": {"id": "open", "name": "Открытая"}, "hidden": false, "salary": null, "address": {"lat": 55.687309, "lng": 37.616261, "raw": null, "city": "Москва", "metro": {"lat": 55.682099, "lng": 37.620917, "line_id": "9", "line_name": "Серпуховско-Тимирязевская", "station_id": "9.85", "station_name": "Нагатинская"}, "street": "Нагорный проезд", "building": "5", "description": null, "metro_stations": [{"lat": 55.682099, "lng": 37.620917, "line_id": "9", "line_name": "Серпуховско-Тимирязевская", "station_id": "9.85", "station_name": "Нагатинская"}]}, "premium": false, "archived": false, "contacts": null, "employer": {"id": "2575", "url": "https://api.hh.ru/employers/2575", "name": "Бэнкс Софт Системс", "trusted": true, "logo_urls": {"90": "https://hhcdn.ru/employer-logo/835799.jpeg", "240": "https://hhcdn.ru/employer-logo/835800.jpeg", "original": "https://hhcdn.ru/employer-logo-original/232339.jpg"}, "alternate_url": "https://hh.ru/employer/2575", "vacancies_url": "https://api.hh.ru/vacancies?employer_id=2575"}, "schedule": {"id": "flexible", "name": "Гибкий график"}, "relations": [], "created_at": "2017-07-03T17:48:06+0300", "department": null, "employment": {"id": "full", "name": "Полная занятость"}, "experience": {"id": "between1And3", "name": "От 1 года до 3 лет"}, "key_skills": [{"name": "Atlassian Jira"}], "description": "<p><strong>Компания BSS </strong>основана в 1994 году и сейчас <strong>занимает лидирующие позиции</strong> на рынке разработки и внедрения автоматизированных систем <strong>дистанционного банковского обслуживания </strong>(ДБО) и <strong>управления корпоративными финансами.</strong></p> <p>Решения BSS используют<strong> более 1700 банков </strong>и филиалов России и ближнего зарубежья, в том числе <strong>свыше 55% банков </strong>уровня TОП 100.</p> <p>Среди клиентов компании крупнейшие банки: ПАО Сбербанк, Банк ВТБ (ПАО), Банк ГПБ (АО), ПАО Банк «ФК Открытие», АО «Россельхозбанк», АО ЮниКредит Банк, ПАО «АК БАРС» БАНК, ПАО АКБ «Связь-Банк», ПАО «УРАЛСИБ», Банк «Возрождение» (ПАО) и др.</p> <p>Мы ищем в свою профессиональную команду Специалиста, готового развиваться и обмениваться экспертизой в нашей дружной команде!</p> <p> </p> <p><strong>Мы предлагаем такой функционал:</strong></p> <ul> <li>Настройка и администрирование систем JIRA и Confluence на уровне приложений и СУБД (до 100 плагинов)</li> <li>Описание и документирование процессов, принципов работы с системами JIRA и Confluence</li> <li>Взаимодействие с внутренними разработчиками по реализации общих задач</li> </ul> <p> </p> <p><strong>Мы ожидаем, что у Вас есть:</strong></p> <ul> <li>Высшее техническое образование</li> <li>Опыт администрирования JIRA, Confluence</li> <li>Знание PL/SQL и Oracle</li> <li>Ориентация на качественный результат</li> <li>Желание и умение работать в команде</li> </ul> <p> </p> <p><strong>У нас такие условия работы: </strong></p> <ul> <li>Офис в районе м.Нагатинская/ м.Тульская</li> <li>Гибкий график работы: 9-18/10-19 (часы работы обсуждаются, их можно корректировать)</li> <li>ТК РФ, белая заработная плата: оклад и бонусы</li> <li>ДМС (включает сеть ведущих клиник и стоматологию, страховку выезжающих за рубеж)</li> <li>Корпоративное обучение</li> <li>Корпоративные мероприятия и подарки, детские праздники</li> <li>Своя футбольная команда и команда по настольному теннису, участие в корпоративных турнирах</li> </ul> <p> </p> <p> </p>", "billing_type": {"id": "standard", "name": "Стандарт"}, "published_at": "2017-07-03T17:48:06+0300", "response_url": null, "alternate_url": "https://hh.ru/vacancy/21815133", "allow_messages": true, "specializations": [{"id": "1.221", "name": "Программирование, Разработка", "profarea_id": "1", "profarea_name": "Информационные технологии, инте